mapper.select:
select * from ( select rownum row_, R.* from ( select pr.process_id, pr.plan_id, pr.process_cus, pr.process_std, pr.process_color, pr.print_num, pr.print_date, pr.print_person, pr.pretreat_num, pr.dye_num, decode(bs.plan_type,3,'无底色印花产品',4,'有底色印花产品') plan_type_name, (case pr.process_status when '10' then '已前处理' when '15' then '已染色' when '20' then '已印花' else '已印花' end) process_status_name from bus_process pr inner join bus_plan bs on pr.plan_id=bs.plan_id
(((pr.process_status='10' or pr.process_status='20' or pr.process_status='25' or pr.process_status='30' or pr.process_status='35' or pr.process_status='40' or pr.process_status='50') and bs.plan_type=3) or (pr.process_status='15' or pr.process_status='20' or pr.process_status='25' or pr.process_status='30' or pr.process_status='35' or pr.process_status='40' or pr.process_status='50') and bs.plan_type=4)
and pr.process_id = #{process_id}
and (pr.process_status = '10' or pr.process_status = '15')
and pr.process_status >= '20'
and process_status >= '10'
)R where rownum <= ${end} )RR where row_ > ${start}

mapper.update:
update bus_process set 
	print_num=#{print_num}, print_date=#{print_date}, print_person=#{print_person}, process_status=#{process_status} 
where process_id = #{process_id}
